import React from 'react';
import './Cards.css';
import CardItem from './CardItem';

function AboutContent() {
  return (
    <div className='cards'>
      <h1>About Pine-Travel</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            <CardItem 
              src='images/bromo.jpg'
              text= "Indonesia is ranked as the world's No. 6 most beautiful country compiled by the London-based travel guide website Rough Guides, making it the best among Asian countries. According to the results of voting submitted by Rough Guides readers, Indonesia was famous for myriad of islands and volcanoes that span across its vast territory. The nation was also rich in cultural heritages from ethnic groups inhabiting thousands of islands in the country. In the voting process, Indonesia left behind two other Asian countries voted by readers, India and Vietnam, at ranks of 13 and 20 respectively."
              path=''
            />
          </ul>
        </div>
      </div>
    </div>
  );
}

export default AboutContent;
