import React from 'react';
import Colors from '../Colors'
import { Link } from 'react-router-dom';
import DetailsThumb from '../DetailThumb';
import '../../index.css';
import '../../App.css';
import '../Content.css';

class Beach extends React.Component{

    state = {
      products: [
        {
          "_id": "1",
          "title": "Pink Beach, Labuan Bajo",
          "src": [
              "../images/labuan.jpg",
              "../images/labuan2.jpg",
              "../images/labuan3.jpg",
              "../images/labuan5.jpg",
              "../images/labuan6.jpg",
              
            ],
          "description": "Inilah beberapa detail lokasi, rute, aktivitas dan biaya yang dibutuhkan untuk wisata ke pink beach, Labuan Bajo.",
          "content": "Pink beach merupakan salah satu pulau yang berada di Pulau Komodo, Flores Barat, Nusa Tenggara Timur. Untuk datang kemari kamu harus menempuh perjalanan darat dan laut karena letaknya yang ada di Pulau Komodo. Pantai ini berjarak 82 kilometer dari Kota Mataram. ", 
          "content2": "Nah, untuk sampai ke Pink beach, Labuan Bajo, kamu harus menaiki pesawat menuju Bandar Udara Internasional Komodo. Kmau bisa menginap semalam dahulu di Labuan Bajo, untuk berangakt dengan kapal keesokan paginya, atau pilih rute pesawat subuh, untuk langsung berlayar ketika tiba di Labuan Bajo. Dari Labuan Bajo, kamu akan berlayar dari Dermaga Loh Liang, Pulau Komodo. Dari Dermaga Loh Liang, biasanya paket wisata kamu sudah termasuk kunjungan ke beberapa lokasi, termasuk pink beach. Ke Pink beach sendiri bisa ditempuh 30 menit menggunakan kapal atau 4 jam trekking melalui tebing karang dan hutan bakau. Mungkin akan lebih lama, akan tetapi panorama yang bisa kamu nikmati lebih indah.", 
          "link": "/BeachDetail",
          "count": 1
        },
        {
            "_id": "2",
            "title": "Perulangan",
            "src": [
                "../images/ampat.jpg",
                "../images/ampat1.jpg",
                "../images/ampat2.jpg",
                "../images/ampat3.jpg",
                "../images/ampat5.jpg",               
              ],
            "description": "UI/UX designing, html css tutorials",
            "content": "Welcome to our channel Dev AT. Here you can learn web designing, UI/UX designing, html css tutorials, css animations and css effects, javascript and jquery tutorials and related so on.",
            "count": 1
          }
      ],
      index: 0
      
    };
  
    myRef = React.createRef();
  
    handleTab = index =>{
      this.setState({index: index})
      const images = this.myRef.current.children;
      for(let i=0; i<images.length; i++){
        images[i].className = images[i].className.replace("active", "");
      }
      images[index].className = "active";
    };
  
    componentDidMount(){
      const {index} = this.state;
      this.myRef.current.children[index].className = "active";
    }
  
  
    render(){
      const {products, index} = this.state;
      return(
        <div className="content">
          {
            products.map(item =>(
              <div className="details" key={item._id}>
                <div className="big-img">
                  <img src={item.src[index]} alt=""/>
                </div>
  
                <div className="box">
                  <div className="row">
                    <h2>{item.title}</h2>
                  </div>
                  <p>{item.description}</p>
                  <p>{item.content}</p>
                  <p>{item.content2}</p>
                  <Link to={item.link}>Pergi</Link>
                  <DetailsThumb images={item.src} tab={this.handleTab} myRef={this.myRef} />
                </div>
              </div>
            ))
          }
        </div>
      );
    };
  }
  
  export default Beach;
